function has_class(s,a){return s.classList?s.classList.contains(a):new RegExp("(^| )"+a+"( |$)","gi").test(s.className)}function add_class(s,a){s.classList?s.classList.add(a):s.className+=" "+a}function remove_class(s,a){s.classList?s.classList.remove(a):s.className=s.className.replace(new RegExp("(^|\\b)"+a.split(" ").join("|")+"(\\b|$)","gi")," ")}function toggle_class(s,a){if(s.classList)s.classList.toggle(a);else{var e=s.className.split(" "),t=e.indexOf(a);t>=0?e.splice(t,1):e.push(a),s.className=e.join(" ")}}var hamburger=document.getElementById("js-navigation-hamburger"),nav=document.getElementById("js-navigation--main__list");hamburger.addEventListener("click",function(s){s.preventDefault(),toggle_class(hamburger,"active"),toggle_class(nav,"active")});